INSERT INTO `wp_postmeta` VALUES ('1003', '209', '_form', 'المادة التدريبية\n\n<p>وثيقة الصلة ببيئة العمل<br />\n   [select Closelyrelatedtotheworkenvironment \"1\" \"2\" \"3\" \"4\" \"5\"]</p>\n\n<p>مصحوبة بأمثلة و حالات عملية<br />\n    [select Accompaniedbyexamplesandpracticalcases \"1\" \"2\" \"3\" \"4\" \"5\"] </p>\n\n<p>كانت متناسبة مع مدة الدورة<br />\n   [select Wasproportionaltothedurationofthesession \"1\" \"2\" \"3\" \"4\" \"5\"] </p>\n\n<p>منظمة من حيث الشكل<br />\n  [select Organizationofform \"1\" \"2\" \"3\" \"4\" \"5\"]</p>\n\n<p>الدورة التدريبية وثيقة الصلة بحاجات  الشركة<br />\n[select Coursecloselyrelatedtotheneedsofthecompany \"1\" \"2\" \"3\" \"4\" \"5\"]</p>\n\n<p>الدورة التدريبية وثيقة الصلة بحاجاتي كموظفbr />\n[select CoursecloselyrelatedtoanemployeeBhajata \"1\" \"2\" \"3\" \"4\" \"5\"]</p>\n\n<p>الدورة التدريبية وثيقة الصلة بحاجات الطرفين  <br />\n   [select Coursecloselyrelatedtotheneedsoftheparties \"1\" \"2\" \"3\" \"4\" \"5\"] </p>\n<br />\nالمدرب\n<p>متمكن من المادة التدريبية<br />\n[select Versedoftrainingmaterial \"1\" \"2\" \"3\" \"4\" \"5\"]</p>\n\n<p>كانت متناسبة مع مدة الدورة<br />\n   [select Capableofmanagingthedialogue \"1\" \"2\" \"3\" \"4\" \"5\"] </p>\n\n<p>لديه مهارات عرض وتقديم جيدة<br />\n   [select Hastheskillstoofferandprovideagood \"1\" \"2\" \"3\" \"4\" \"5\"]</p>\n\n<p>لديه اسلوب جيد في التدريب<br />\n  [select Hasagoodstyleintraining \"1\" \"2\" \"3\" \"4\" \"5\"]</p>\n</br>\nبيئة التدريب\n\n<p>مكان التدريب و التجهيزات المستعملة ملائمة<br />\n[select Placeoftrainingandappropriateequipmentused \"1\" \"2\" \"3\" \"4\" \"5\"]</p>\n</br>\n\nنتائج التدريب\n\n<p>الدورة حققت الأهداف المتوقعة<br />\n [select Sessionhasachievedtheexpectedgoals \"1\" \"2\" \"3\" \"4\" \"5\"]</p>\n</br>\n\n<p>تقييمك للدورة بشكل عام<br />\n[select Yourcycleingeneral \"1\" \"2\" \"3\" \"4\" \"5\"]</p>\n</br>\n\n<p>انصح زملائي بحضور هذه الدورة<br />\n [select Iadvisemycolleaguestoattendthissession \"1\" \"2\" \"3\" \"4\" \"5\"]</p>\n</br>\n\n<p>ما هي اقتراحاتك لتحسين الدورة مستقبلا؟<br />\n [textarea Whatareyoursuggestionstoimprovethecourseinthefuture]</p>\n</br>\n\n\n<p>[submit \"إرسال\"]</p>');
